package lista_execicios;

public class Exe01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int intNum1, intNum2;
		float fltNum1, fltNum2;
		
		intNum1 = 20/2;
		System.out.println(intNum1);
		System.out.println();
		
		intNum1 = 10/20;
		System.out.println(intNum1);
		System.out.println();
		
		fltNum1 = 10/20;
		System.out.println(fltNum1);
		System.out.println();
		
		fltNum2 = (float)10/20;
		System.out.println(fltNum2);
		System.out.println();
		
		fltNum1 = (float)10/20;
		System.out.println(fltNum1);
		System.out.println();
	}
}
